private addFirmaToPDF(doc: any, currentY: number): number {

  const parser = new DOMParser();
  const htmlDocument = parser.parseFromString(this.firma, 'text/html');

  const paragraphs = htmlDocument.querySelectorAll('p');

  Array.from(paragraphs).forEach(paragraph => {

      const textContent = paragraph.textContent || '';
      if (textContent.trim()) {
          doc.text(textContent, 10, currentY);
          currentY += 10;
      }

      const imgElement = paragraph.querySelector('img');
      if (imgElement) {
          const imgBase64 = imgElement.src;
          doc.addImage(imgBase64, 'PNG', 10, currentY, 90, 40);
          currentY += 170;
      }
  });

  return currentY;
}


  loadImageAsDataURL(relativePath: string): Promise<string> {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.src = relativePath;

      img.onload = () => {
        const canvas = document.createElement('canvas');
        canvas.width = img.width;
        canvas.height = img.height;

        const ctx = canvas.getContext('2d');
        if (ctx) {
          ctx.drawImage(img, 0, 0);
          const dataURL = canvas.toDataURL('image/png');
          resolve(dataURL);
        } else {
          reject(new Error('Error al obtener el contexto del canvas.'));
        }
      };

      img.onerror = (error) => {
        reject(new Error('Error al cargar la imagen: ' + error));
      };
    });
  }

  private addText(doc: any, text: string, y: number): number {

    if (y > 280) {
      doc.addPage();
      y = 10;
    }
    doc.text(text, 10, y);

    return y + 5;
  }

  private addSeccionText(doc: any, text: string, y: number): number {

    if (y > 280) {
      doc.addPage();
      y = 10;
    }
    doc.text(text, 10, y);

    return y + 15;
  }

  private addMultilineText(doc: any, text: string, y: number, maxWidth: number, pdfDoc: any): number {
    const splitText = doc.splitTextToSize(text, maxWidth);
    const lineHeight = 10;

    splitText.forEach((line: string) => {
      if (y > 280) {
        pdfDoc.addPage();
        y = 10;
      }
      doc.text(line, 10, y);
      y += lineHeight;
    });

    return y;
  }


